import React, { useState, useRef } from 'react';
import { Camera, Barcode, Cat, Dog, Info } from 'lucide-react';
import { Alert, AlertTitle, AlertDescription } from '@/components/ui/alert';

const App = () => {
  const [animal, setAnimal] = useState('');
  const [barcode, setBarcode] = useState('');
  const [result, setResult] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const fileInputRef = useRef();

  const checkBarcode = async () => {
    setIsLoading(true);
    setError('');
    try {
      const response = await fetch('http://localhost:5000/api/check-barcode', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ barcode, animal }),
      });
      const data = await response.json();
      if (response.ok) {
        setResult(data.result);
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Erreur de connexion au serveur');
    }
    setIsLoading(false);
  };

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setIsLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('image', file);
    formData.append('animal', animal);

    try {
      const response = await fetch('http://localhost:5000/api/scan-image', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      if (response.ok) {
        setBarcode(data.barcode);
        setResult(data.result);
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Erreur de connexion au serveur');
    }
    setIsLoading(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-orange-50 to-orange-100 p-4">
      <div className="max-w-md mx-auto bg-white rounded-2xl shadow-lg overflow-hidden transform transition-all hover:scale-[1.02]">
        <div className="p-8">
          <h1 className="text-3xl font-bold mb-8 text-center text-orange-600">
            Vérificateur d'aliments pour animaux
          </h1>

          <div className="space-y-6">
            <div className="flex gap-3 justify-center">
              {[
                { id: 'chat', icon: <Cat size={24} />, label: 'Chat' },
                { id: 'chien', icon: <Dog size={24} />, label: 'Chien' },
                { id: 'lapin', icon: '🐰', label: 'Lapin' }
              ].map(({ id, icon, label }) => (
                <button
                  key={id}
                  onClick={() => setAnimal(id)}
                  className={`flex items-center gap-2 px-6 py-3 rounded-xl transition-all transform hover:scale-105 ${
                    animal === id
                      ? 'bg-orange-500 text-white shadow-md'
                      : 'bg-orange-100 text-orange-800 hover:bg-orange-200'
                  }`}
                >
                  {icon} {label}
                </button>
              ))}
            </div>

            <div className="space-y-4">
              <div className="flex gap-2">
                <input
                  type="text"
                  value={barcode}
                  onChange={(e) => setBarcode(e.target.value)}
                  placeholder="Code-barres du produit"
                  className="flex-1 p-3 border-2 border-orange-200 rounded-xl focus:border-orange-400 focus:ring-2 focus:ring-orange-200 outline-none transition-all"
                />
                <button
                  onClick={checkBarcode}
                  disabled={!animal || !barcode || isLoading}
                  className="bg-orange-500 text-white p-3 rounded-xl hover:bg-orange-600 disabled:bg-gray-300 transition-all"
                >
                  <Barcode size={24} />
                </button>
              </div>

              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <div className="w-full border-t border-orange-200"></div>
                </div>
                <div className="relative flex justify-center text-sm">
                  <span className="px-2 bg-white text-orange-600">ou</span>
                </div>
              </div>

              <button
                onClick={() => fileInputRef.current?.click()}
                disabled={!animal || isLoading}
                className="w-full flex items-center justify-center gap-2 bg-orange-500 text-white p-4 rounded-xl hover:bg-orange-600 disabled:bg-gray-300 transition-all transform hover:scale-[1.02]"
              >
                <Camera size={24} />
                Scanner un code-barres
              </button>
              <input
                type="file"
                accept="image/*"
                capture="environment"
                ref={fileInputRef}
                onChange={handleImageUpload}
                className="hidden"
              />
            </div>

            {isLoading && (
              <div className="text-center text-orange-600 animate-pulse">
                Chargement...
              </div>
            )}

            {error && (
              <Alert variant="destructive" className="border-2 border-red-200 bg-red-50">
                <AlertTitle className="text-red-700">Erreur</AlertTitle>
                <AlertDescription className="text-red-600">{error}</AlertDescription>
              </Alert>
            )}

            {result && (
              <Alert
                className={`border-2 ${
                  result.includes('✅')
                    ? 'border-green-200 bg-green-50'
                    : 'border-red-200 bg-red-50'
                }`}
              >
                <Info className="h-5 w-5" />
                <AlertTitle className={result.includes('✅') ? 'text-green-700' : 'text-red-700'}>
                  Résultat
                </AlertTitle>
                <AlertDescription
                  className={`whitespace-pre-line ${
                    result.includes('✅') ? 'text-green-600' : 'text-red-600'
                  }`}
                >
                  {result}
                </AlertDescription>
              </Alert>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
